
import { grey } from "@mui/material/colors";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import style from "./Product.module.css"
import FavoriteIcon from "@material-ui/icons/Favorite"
const getApiData = () =>{
  return axios.get("https://trouper-org.onrender.com/products") 
}
 
function ProductPage() {
   
    const [data,setdata]=useState([]);
  
  const [v, setv]=useState("")
  const navigate=useNavigate()
  
  let x=localStorage.getItem("pdata")
 
 

 
useEffect(()=>{
    getApiData().then((res)=>{
    setdata(res.data)
     })
  

   
   },[])

 
// if(l==0){
//     navigate("/")
    
// }
 
   

 
  if(v==="inc" ){
    data.sort(function(a,b){
      var x=a.rating;
      var y=b.rating;
      if(x>y){
        return 1;
      }
      if(x<y){
        return -1;
      }
      return 0;

    });
  }

  if(v==="dic"){
    data.sort(function(a,b){
      var x=a.rating;
      var y=b.rating;
      if(x>y){
        return -1;
      }
      if(x<y){
        return 1;
      }
      return 0;

    });
  }
  
  if(v==="asc" ){
    data.sort(function(a,b){
      var x=a.price;
      var y=b.price;
      if(x>y){
        return 1;
      }
      if(x<y){
        return -1;
      }
      return 0;

    });
  }

  if(v==="des"){
    data.sort(function(a,b){
      var x=a.price;
      var y=b.price;
      if(x>y){
        return -1;
      }
      if(x<y){
        return 1;
      }
      return 0;

    });
  }
    

 return (
     <>
     <h1 className={style.categorytitle}>{x}</h1>
     <div className={style.filter}>
        <div className={style.slctdiv}>
        <select onChange={(e)=>setv(e.target.value)}  value={v} className={style.slct}>
          <option value="">Sort By Price</option>
          <option value="asc">ASC</option>
          <option value="des">DESC</option>
        </select>
        <select onChange={(e)=>setv(e.target.value)}  value={v} className={style.slct}>
        <option value="">Sort By Rating</option>
          <option value="inc">ASC</option>
          <option value="dic">DESC</option>
          </select>
        </div>

        <div>18 services available</div>
     
     </div>
  
      
        
     
         <div className={style.productdiv}>
           {
            data?.filter((el)=>el.category==x).map((el)=>(

              <div key={el.id} className={style.divbox}>
                  
                  <img src="data:image/jpeg;base64," alt="" />
                   <div className={style.bagdiv}>
                   <div className={style.sdiv}>
                        <img src={el.profilePic} alt="" />
                        <div className={style.idbox}>
                              <p>{el.name}</p>
                              <span className={style.rating1}>{el.lavel}</span>
                        </div>
                  </div>
                  <Link to={`/product/${el._id}`}>{el.title}</Link>
                  <span className={style.rating}>{el.rating}</span>
                   </div>
                 <hr />
                  <div className={style.pricediv}>
                    <FavoriteIcon sx={{color:grey[500]}}/>
                     <span className={style.price}>
                      <p>STARTING AT</p>
                       {el.price}
                    </span>
                  </div>
              </div>
            ))
           }

         </div>
 
     </>
  )
}

 
  export default ProductPage



 
 
 
